CREATE TABLE `master_apotek` ( `id` int(11) NOT NULL AUTO_INCREMENT, `nama` varchar(50) NOT NULL, `alamat_jalan` varchar(100) NOT NULL, `alamat_kelurahan` varchar(100) NOT NULL, `alamat_kecamatan` varchar(100) NOT NULL, `alamat_kota` varchar(100) NOT NULL, `alamat_provinsi` varchar(100) NOT NULL, `latitude` varchar(255) NOT NULL, `longitude` varchar(255) NOT NULL, `kode_pos` varchar(10) DEFAULT NULL, `telp` varchar(20) DEFAULT NULL, `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updated_at` timestamp NULL DEFAULT NULL, `created_by` int(11) NOT NULL, `updated_by` int(11) DEFAULT NULL, `aktif` tinyint(1) NOT NULL DEFAULT '0', PRIMARY KEY (`id`) );